import {NativeModules, NativeEventEmitter} from "react-native";

const RNSpeech = NativeModules.RNSpeech;
const Emitter = new NativeEventEmitter(RNSpeech);

const requestAuthorization = (cb) => {
    return RNSpeech.requestAuthorization(err=>{
        cb && cb(err);
    });
}

const startRecord = () => {
    return RNSpeech.startRecord();
}

const stopRecord = (cb) => {
    return RNSpeech.stopRecord(cb);
}

const addListener = (eventType, cb) => {
    return Emitter.addListener(eventType, cb);
}

export default {
    startRecord,
    stopRecord,
    requestAuthorization,
    addListener
}
